import { useEffect, useMemo, useState } from "react";
import { ThirdwebSDK } from "@3rdweb/sdk";
import { ethers } from "ethers";

// import thirdweb
import { useWeb3 } from "@3rdweb/hooks";

// We instantiate the sdk on Rinkeby.
const sdk = new ThirdwebSDK("rinkeby");

const bundleDropModule = sdk.getBundleDropModule(
    "0x9D02F9AFee94d5CE7d6eF3922fAd2d35482C986B",
);

const tokenModule = sdk.getTokenModule(
    "0xEbf650c7108D77A79c498E21D524427B982Db502",
);

// Our voting contract.
const voteModule = sdk.getVoteModule(
    "0x8862eaa4a643c2AbfF0F2690B4658d2733585E9E",
  );


const App = () => {
  // Use the connectWallet hook thirdweb gives us.
  const { connectWallet, address, error, provider } = useWeb3();
  console.log("üëã Address:", address)

    // State variable for us to know if user has our NFT.
    const [hasClaimedNFT, setHasClaimedNFT] = useState(false);

    // isClaiming lets us easily keep a loading state while the NFT is minting.
  const [isClaiming, setIsClaiming] = useState(false);

    // Holds the amount of token each member has in state.
    const [memberTokenAmounts, setMemberTokenAmounts] = useState({});
    // The array holding all of our members addresses.
    const [memberAddresses, setMemberAddresses] = useState([]);

    const [proposals, setProposals] = useState([]);
    const [isVoting, setIsVoting] = useState(false);
    const [hasVoted, setHasVoted] = useState(false);

    // Retrieve all our existing proposals from the contract.
    useEffect(async () => {
    if (!hasClaimedNFT) {
        return;
    }
    // A simple call to voteModule.getAll() to grab the proposals.
    try {
        const proposals = await voteModule.getAll();
        setProposals(proposals);
        console.log("üåà Proposals:", proposals);
    } catch (error) {
        console.log("failed to get proposals", error);
    }
    }, [hasClaimedNFT]);

    // We also need to check if the user already voted.
    useEffect(async () => {
    if (!hasClaimedNFT) {
        return;
    }

    // If we haven't finished retrieving the proposals from the useEffect above
    // then we can't check if the user voted yet!
    if (!proposals.length) {
        return;
    }

    // Check if the user has already voted on the first proposal.
    try {
        const hasVoted = await voteModule.hasVoted(proposals[0].proposalId, address);
        setHasVoted(hasVoted);
        if(hasVoted) {
        console.log("ü•µ User has already voted");
        } else {
        console.log("üôÇ User has not voted yet");
        }
    } catch (error) {
        console.error("Failed to check if wallet has voted", error);
    }
    }, [hasClaimedNFT, proposals, address]);

    // A fancy function to shorten someones wallet address, no need to show the whole thing. 
    const shortenAddress = (str) => {
    return str.substring(0, 6) + "..." + str.substring(str.length - 4);
    };

    // This useEffect grabs all the addresses of our members holding our NFT.
    useEffect(async () => {
    if (!hasClaimedNFT) {
        return;
    }

    // Just like we did in the 7-airdrop-token.js file! Grab the users who hold our NFT
    // with tokenId 0.
    try {
        const memberAddresses = await bundleDropModule.getAllClaimerAddresses("0");
        setMemberAddresses(memberAddresses);
        console.log("üöÄ Members addresses", memberAddresses);
    } catch (error) {
        console.error("failed to get member list", error);
    }
    }, [hasClaimedNFT]);

    // This useEffect grabs the # of token each member holds.
    useEffect(async () => {
    if (!hasClaimedNFT) {
        return;
    }

    // Grab all the balances.
    try {
        const amounts = await tokenModule.getAllHolderBalances();
        setMemberTokenAmounts(amounts);
        console.log("üëú Amounts", amounts);
    } catch (error) {
        console.error("failed to get token amounts", error);
    }
    }, [hasClaimedNFT]);

    // Now, we combine the memberAddresses and memberTokenAmounts into a single array
    const memberList = useMemo(() => {
    return memberAddresses.map((address) => {
        return {
        address,
        tokenAmount: ethers.utils.formatUnits(
            // If the address isn't in memberTokenAmounts, it means they don't
            // hold any of our token.
            memberTokenAmounts[address] || 0,
            18,
        ),
        };
    });
    }, [memberAddresses, memberTokenAmounts]);

    // The signer is required to sign transactions on the blockchain.
  // Without it we can only read data, not write.
  const signer = provider ? provider.getSigner() : undefined;

    // Another useEffect!
    useEffect(() => {
        // We pass the signer to the sdk, which enables us to interact with
        // our deployed contract!
        sdk.setProviderOrSigner(signer);
      }, [signer]);


    useEffect(async () => {
      // If they don't have an connected wallet, exit!
      if (!address) {
        return;
      }

      // Check if the user has the NFT by using bundleDropModule.balanceOf
      const balance = await bundleDropModule.balanceOf(address, "0");

      try {
        // If balance is greater than 0, they have our NFT!
        if(balance.gt(0)) {
            setHasClaimedNFT(true);
            console.log("üåü this user has a membership NFT!");
        } else {
            setHasClaimedNFT(false);
            console.log("üò≠ this user doesn't have a membership NFT.")
        }
      } catch (error) {
          setHasClaimedNFT(false);
          console.error("failed to nft balance", error);
      }
    }, [address]);

    const mintNft = async () => {
        setIsClaiming(true);
        try {
          // Call bundleDropModule.claim("0", 1) to mint nft to user's wallet.
          const result = await bundleDropModule.claim("0",1);
          console.log('result', result);
          // Set claim state.
          setHasClaimedNFT(true);
          // Show user their fancy new NFT!
          console.log(`üåä Successfully Minted! Check it out on OpenSea: https://testnets.opensea.io/assets/${bundleDropModule.address}/0`);
        } catch (error) {
          console.error("failed to claim", error);
        } finally {
          // Stop loading state.
          setIsClaiming(false);
        }
      }

    if (!address) {
        return (
          <div className="landing">
            <h1>Welcome to NarutoDAO</h1>
            <button onClick={() => connectWallet("injected")} className="btn-hero">
              Connect your wallet
            </button>
          </div>
        );
      }



  // This is the case where we have the user's address
  // which means they've connected their wallet to our site!
//   return (
//     <div className="landing">
//       <h1>üëÄ wallet connected, now what!</h1>
//     </div>);

    if (!address) {
        return (
        <div className="landing">
            <h1>Welcome to NarutoDAO</h1>
            <button onClick={() => connectWallet("injected")} className="btn-hero">
            Connect your wallet
            </button>
        </div>
        );
    }

    // Add this little piece!
    // If the user has already claimed their NFT we want to display the interal DAO page to them
    // only DAO members will see this. Render all the members + token amounts.
    if (hasClaimedNFT) {
        return (
        <div className="member-page">
            <h1>üç™DAO Member Page</h1>
            <p>Congratulations on being a member</p>
            <div>
            <div>
                <h2>Member List</h2>
                <table className="card">
                <thead>
                    <tr>
                    <th>Address</th>
                    <th>Token Amount</th>
                    </tr>
                </thead>
                <tbody>
                    {memberList.map((member) => {
                    return (
                        <tr key={member.address}>
                        <td>{shortenAddress(member.address)}</td>
                        <td>{member.tokenAmount}</td>
                        </tr>
                    );
                    })}
                </tbody>
                </table>
            </div>
            </div>
        </div>
        );
    };

  // Render mint nft screen.
  return (
    <div className="mint-nft">
      <h1>Mint your free üç™DAO Membership NFT</h1>
      <button
        disabled={isClaiming}
        onClick={() => mintNft()}
      >
        {isClaiming ? "Minting..." : "Mint your nft (FREE)"}
      </button>
    </div>
  );
};

export default App;
