/**
 * @internal
 */
function getGasStationUrl(chainId?: number): string | null {
  if (!chainId) {
    return null;
  }

  if (chainId === ChainId.Polygon) {
    return "https://gasstation-mainnet.matic.network";
  }

  return null;
}

/**
 *
 * @returns the gas price
 * @internal
 */
export async function getGasPriceForChain(
  chainId: number,
  speed: string,
  maxGasPrice: number,
): Promise<number | null> {
  const gasStationUrl = getGasStationUrl(chainId);
  if (!gasStationUrl) {
    return null;
  }
  try {
    const data = await (await fetch(gasStationUrl)).json();
    let gas = data[speed];

    if (chainId === ChainId.Polygon || chainId === ChainId.Mumbai) {
      // the minimum gas on chain is 30, +1 for priority. prevent gas station report false price and cause tx to fail.
      gas = Math.max(gas, 31);
    }
    if (gas > 0) {
      return Math.min(gas, maxGasPrice);
    }
  } catch (e) {
    console.error("failed to fetch gas", e);
  }
  return null;
}
const genericMessage = "Invariant Violation";

const {
  setPrototypeOf = function (obj: any, proto: any) {
    obj.__proto__ = proto;
    return obj;
  },
} = Object as any;

/**
 * Error that may get thrown when an invariant assummption fails.
 * @public
 */
export class InvariantError extends Error {
  /**
   * @internal
   */
  framesToPop = 1;
  /**
   * @internal
   */
  name = genericMessage;
  /**
   * @internal
   */
  constructor(message: string = genericMessage) {
    super(
      typeof message === "number"
        ? `${genericMessage}: ${message} (see https://github.com/apollographql/invariant-packages)`
        : message,
    );
    setPrototypeOf(this, InvariantError.prototype);
  }
}
/**
 * @internal
 * @param condition - any truthy condition to assert
 * @param message  - optional message to use if the condition is falsy
 */
export function invariant(condition: any, message?: string): asserts condition {
  if (!condition) {
    throw new Error(message);
  }
}
import { TypedDataField } from "@ethersproject/abstract-signer";
import { JsonRpcProvider, JsonRpcSigner } from "@ethersproject/providers";
import { ethers, Signer } from "ethers";

/**
 * @internal
 */
export interface EIP712StandardDomain {
  name: string;
  version: string;
  chainId: number;
  verifyingContract: string;
}

/**
 * @internal
 */
export interface EIP712PolygonDomain {
  name: string;
  version: string;
  verifyingContract: string;
  salt: string;
}

/**
 * @internal
 */
export type EIP712Domain = EIP712StandardDomain | EIP712PolygonDomain;

/**
 * eip712 sign typed data with different wallet handling including ledger live
 * @internal
 */
export async function signTypedData(
  signer: Signer,
  domain: EIP712Domain,
  types: Record<string, Array<TypedDataField>>,
  message: Record<string, any>,
) {
  const provider = signer?.provider as JsonRpcProvider;
  if (!provider) {
    throw new Error("missing provider");
  }

  const payload = ethers.utils._TypedDataEncoder.getPayload(
    domain,
    types,
    message,
  );

  let signature = "";

  // an indirect way for accessing walletconnect's underlying provider
  if ((provider as any)?.provider?.isWalletConnect) {
    signature = await provider.send("eth_signTypedData", [
      (await signer.getAddress()).toLowerCase(),
      JSON.stringify(payload),
    ]);
  } else {
    try {
      signature = await (signer as JsonRpcSigner)._signTypedData(
        domain,
        types,
        message,
      );
    } catch (err: any) {
      if (err?.message?.includes("Method eth_signTypedData_v4 not supported")) {
        signature = await provider.send("eth_signTypedData", [
          (await signer.getAddress()).toLowerCase(),
          JSON.stringify(payload),
        ]);
      } else {
        throw err;
      }
    }
  }

  // fix ledger live where signature result in v = 0, 1. ethers magically fix it in split/join.
  return {
    payload,
    signature: ethers.utils.joinSignature(
      ethers.utils.splitSignature(signature),
    ),
  };
}
export enum EventType {
  /**
   * The event is triggered when the transaction is submitted and completed.
   * The transaction is submitted when it is sent to the relayer or the blockchain.
   * The transaction is completed when it is confirmed by the blockchain.
   *
   * status: "submitted" | "completed"
   * transactionHash: string of the transaction hash
   */
  Transaction = "transaction",

  /**
