  public async totalUnclaimedSupply(): Promise<BigNumber> {
    if (await this.isV0()) {
      return this.v1Module.totalUnclaimedSupply();
    }
    return (await this.readOnlyContract.nextTokenIdToMint()).sub(
      await this.totalClaimedSupply(),
    );
  }

